mapper.select:
SELECT
    td.bd_loan_id,
    tos.order_status_id,
    tos.order_id,
    tos.vbs_bid,
    tos.vbs_notify_number,
    inf.cust_id,
    inf.cust_name,
    inf.id_no,
    inf.cust_mobile,
    td.to_bank_name,
    td.bank_card_num,
    tos.create_time,
    tos.loan_rel_time,
    td.amount,
    tco.request_apply_period AS loan_period,
    tos.order_status,
    td.order_des,
    tas.handle_status
FROM
    t_order_status tos
LEFT JOIN t_order td ON td.order_id = tos.order_id
LEFT JOIN t_custinfo inf ON inf.cust_id = td.cust_id
LEFT JOIN t_create_contract tco ON tco.loan_id = td.bd_loan_id
LEFT JOIN t_account_status tas ON tas.loan_id = td.bd_loan_id

        tos.create_time  >= #{applyBegin,jdbcType=VARCHAR}
        and tos.create_time  <= #{applyEnd,jdbcType=VARCHAR}

            and tos.loan_rel_time  >= #{loanBegin}


            and tos.loan_rel_time  <= #{loanEnd}


            and tos.vbs_bid = #{vbsId}


            and td.cust_id = #{custId}


            and inf.cust_name = #{custName}


            and td.bank_card_num = #{custCardNo}


            and inf.id_no = #{custIdNo}


            and inf.cust_mobile = #{custMobile}


            and td.bd_loan_id = #{bdLoanId}


            and tos.order_status= #{loanStatus}


            and tas.handle_status = #{financialStatus}

SELECT DISTINCT os.vbs_bid, o.bd_loan_id, o.id_no, l.loan_form, o.cust_id, c.cust_name, c.cust_mobile, o.to_bank_name, o.bank_card_num, o.loan_time, l.start_date, l.repay_day, o.loan_period, o.amount, cq.Day_Rate, cq.Formalities_Rate, c.cust_level, ( l.prin_total + l.int_total + l.pnlt_int_total + l.fund_fee_total ) AS owe_now FROM t_order o LEFT JOIN t_order_status os ON o.order_id = os.order_id LEFT JOIN t_custinfo c ON o.id_no = c.id_no LEFT JOIN t_credit_quota cq ON o.cust_id = cq.cust_id AND cq.quota_status = 1 LEFT JOIN t_loan l ON o.bd_loan_id = l.loan_id
os.vbs_bid = #{vbsId}
and o.bd_loan_id = #{bdLoanId}

SELECT t.issue_id AS issue_id, tor.bd_loan_id, t.cur_date, t.issue_type AS exception_type, t.issue_time AS issue_time, t.handle_status AS handle_status, tos.vbs_bid AS vbs_id, t.handle_person, t.handle_time, t.handle_msg, t.issue_msg AS exception_msg FROM ( SELECT * FROM t_account_issue WHERE ( handle_status = 0 OR handle_status = 1 )) t
LEFT JOIN t_order tor ON tor.bd_loan_id = t.loan_id LEFT JOIN t_order_status tos ON tos.order_id = tor.order_id
tos.vbs_bid = #{vbsId}
